mapper.select:
select *
from orders
where user_id = #{userId}

    and status = #{status}

order by order_time desc

select *
from orders


        and number like concat('%', #{number}, '%')


        and phone like concat('%', #{phone}, '%')


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

select *
from orders
where status = #{status}
and order_time < #{time}

select
    date(order_time) as orderDate,
    count(*) as cnt
from orders
where status = #{status}
  and order_time >= #{begin}
  and order_time < #{end}
group by date(order_time)

select count(*) from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time < #{end}

select sum(amount) from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time < #{end}
